INSERT INTO `posts` (`id`, `title`, `content`, `thumbnail`, `user_id`, `cat_id`, `deleted_at`, `created_at`, `updated_at`, `status`, `slug`) VALUES
(12, 'Aorus Model X – Sức mạnh mãnh hổ, nhận quà bùng nổ', 'Nhân dịp năm mới Nhâm Dần 2022, GIGABYTE ra mắt chương trình khuyến mãi hấp dẫn dành cho sản phẩm máy tính đồng bộ mới toanh AORUS MODEL X.\r\n\r\nNgười dùng cuối khi mua AORUS model X ( AMXI9N8A-2171 / 12900K / 3080 / 32G DDR5 / Z690 ) nhận ngay quà tặng 01 ghế gaming AORUS AGC310\r\n\r\nAORUS MODEL X là máy tính chơi game cao cấp sử dụng nền tảng phần cứng mới nhất (Bo mạch chủ Z690 AORUS MASTER / CPU Core i9 12900K / VGA RTX 3080 / RAM 32GB DDR5) mang tới hiệu năng cực cao với khả năng tản nhiệt tốt, độ ồn thấp giúp người sử dụng có được trải nghiệm đỉnh cao nhất.\r\n\r\nAORUS Model X – Sức Mạnh Mãnh Hổ, Nhận Quà Bùng Nổ\r\n\r\nThời gian áp dụng: 02/02/2022 tới 03/03/2022  \r\n\r\nTrong thời gian khuyến mãi, khi bạn mua AORUS MODEL X tại các đại lý áp dụng chương trình, bạn sẽ nhận được quà tặng là chiếc ghế gaming mới ra mắt AORUS AGC310 trị giá 6,500,000 đồng.\r\n\r\nAORUS AGC310 sử dụng chất liệu cao cấp, thoải mái khi ngồi và độ bền được chứng nhận bởi Hiệp hội các nhà sản xuất và kinh doanh đồ nội thất (BIFMA).', 'images/posts/suc-manh-manh-ho-nhan-qua-bung-no.png', 1, 22, NULL, '2022-07-10 15:01:08', '2022-07-10 15:01:08', 'Công khai', 'aorus-model-x-suc-manh-manh-ho-nhan-qua-bung-no'),
(16, 'Mua càng nhiều linh kiện – nhận càng nhiều quà hấp dẫn', 'Chung tay cùng cộng đồng, giãn cách xã hội phòng chống n-Covid 19, Asus cùng Phúc Anh triển khai chương trình khuyến mãi “Làm việc tại nhà, chơi game tại gia” - Tặng combo quà tặng Asus ROG độc quyền khi khách hàng mua các sản phẩm linh kiện Asus tại Phúc Anh trong tháng 6 này.\r\nThời gian khuyến mại: Từ ngày 01/06/2021 đến ngày 30/06/2021 hoặc đến khi hết quà\r\n\r\nHạn chót đăng ký nhận quà: Ngày 05/07/2021\r\n\r\nĐối tượng khuyến mại: Tất cả các khách hàng\r\n\r\nĐịa điểm áp dụng khuyến mại: Tất cả cơ sở của Phúc Anh Smart World\r\n\r\nNội dung chương trình:\r\n\r\n- Tặng combo khẩu trang, nón, áo mưa, áo thun ROG khi mua combo Bo mạch chủ + Card đồ họa + Case + Nguồn\r\n\r\nĐối với áo thun sẽ có 3 mẫu và 3 size S-M-L cho các bạn lựa chọn, ưu tiên những bạn đăng ký nhận quà sớm sẽ chọn size và màu trước, tham khảo kích thước size áo tại link sau: https://bit.ly/310ypF3\r\n\r\n- Tặng kèm thêm bình giữ nhiệu Asus x Gundam khi mua thêm 1 trong các sản phẩm trị giá trên 2.000.000 VNĐ sau đây: Tản nhiệt/ Màn hình/ Thiết bị mạng/ Chuột/ Bàn phím/ Tai nghe.\r\n\r\n- Khi đăng ký thành công, mỗi khách hàng chỉ được nhận 1 quà tặng bình nước Gundam trong suốt thời gian CTKM diễn ra, không áp dụng cộng dồn.', 'images/posts/image001.png', 1, 26, NULL, '2022-07-10 15:08:45', '2022-07-10 15:08:45', 'Công khai', 'mua-cang-nhieu-linh-kien-nhan-cang-nhieu-qua-hap-dan'),
(17, 'Mua màn hình  Aorus 4K - Tặng đồng hồ Garmin Instinct Esports', '<p><span style=\"font-size: 12pt;\">Trong thời gian khuyến m&atilde;i, khi mua m&agrave;n h&igrave;nh AORUS FO48U, kh&aacute;ch h&agrave;ng sẽ được tặng đồng hồ thể thao cực chất Garmin Instinct ESport. Trong thời gian khuyến m&atilde;i, khi mua m&agrave;n h&igrave;nh AORUS FI32U, kh&aacute;ch h&agrave;ng sẽ được tặng Combo Vali AORUS (Bao gồm AORUS Chibi, AORUS Cup &amp; Vali AORUS)</span></p>\r\n<p><span style=\"font-size: 12pt;\">Nh&acirc;n dịp ra mắt thế hệ m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh cực đ&igrave;nh AORUS 4K với khẩu hiệu AORUS 4K! Pro Gaming On, GIGABYTE Việt Nam ra mắt chương tr&igrave;nh khuyến m&atilde;i hấp dẫn.</span></p>\r\n<p><span style=\"font-size: 12pt;\">Thời gian chương tr&igrave;nh:&nbsp;</span></p>\r\n<p><span style=\"font-size: 12pt;\">Từ ng&agrave;y 01/06 &ndash; 30/06/2021</span></p>\r\n<p><span style=\"font-size: 12pt;\">Nội dung chương tr&igrave;nh:&nbsp;</span></p>\r\n<ul>\r\n<li>\r\n<p><span style=\"font-size: 12pt;\">Trong thời gian khuyến m&atilde;i, khi mua m&agrave;n h&igrave;nh&nbsp;AORUS FO48U, kh&aacute;ch h&agrave;ng sẽ được tặng đồng hồ thể thao cực chất&nbsp;Garmin Instinct ESport</span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: 12pt;\">Trong thời gian khuyến m&atilde;i, khi mua m&agrave;n h&igrave;nh&nbsp;AORUS FI32U, kh&aacute;ch h&agrave;ng sẽ được tặng&nbsp;Combo Vali AORUS&nbsp;(Bao gồm AORUS Chibi, AORUS Cup &amp; Vali AORUS)</span></p>\r\n</li>\r\n</ul>', 'images/posts/banner-mua-man-tang-dong-ho.jpg', 1, 23, NULL, '2022-07-10 15:09:56', '2022-07-18 04:24:55', 'Công khai', 'mua-man-hinh-aorus-4k-tang-dong-ho-garmin-instinct-esports'),
(18, 'Mua màn hình Asus ProArt tặng ngay bộ sản phẩm Adobe ', 'GIGABYTE hân hạnh ra mắt chương trình khuyến mãi mua màn hình khủng tặng quà khủng\r\nNhân dịp trình làng thế hệ màn hình AORUS hoàn toàn mới, GIGABYTE chính thức ra mắt chương trình khuyến mãi hấp dẫn cho người dùng cuối.\r\n\r\nThời gian chương trình:\r\nTừ ngày 02/04/2021 đến hết ngày 22/04/2021\r\n\r\nChi tiết chương trình:\r\nKhi mua màn hình AORUS FV43U, bạn sẽ được tặng ghế gaming AORUS AGC300\r\nKhi mua màn hình AORUS FI32Q, bạn sẽ được tặng AORUS Book phiên bản giới hạn\r\nKhi mua màn hình AORUS FI27Q-X, bạn sẽ được tặng AORUS Book', 'images/posts/manhinh2k.jpg', 1, 24, NULL, '2022-07-17 02:54:31', '2022-07-17 03:07:51', 'Công khai', 'mua-man-hinh-asus-proart-tang-ngay-bo-san-pham-adobe'),
(19, 'Intel Core thế hệ thứ 12  Alder Lake-S  hỗ trợ bộ nhớ DDR5', '<p><span style=\"font-size: 12pt;\">D&ograve;ng CPU d&agrave;nh cho m&aacute;y t&iacute;nh để b&agrave;n hế hệ thứ&nbsp;12 của Intel sắp ra mắt c&oacute; t&ecirc;n&nbsp;Alder Lake-S được đồn đại l&agrave; sẽ hỗ trợ bộ nhớ DDR5.Cả AMD v&agrave; INTEL đều chưa ch&iacute;nh thức x&aacute;c nhận hỗ trợ DDR5 cho c&aacute;c kiến ​​tr&uacute;c sắp tới của họ.&nbsp;Điều đ&aacute;ng ch&uacute; &yacute; l&agrave; cả hai c&ocirc;ng ty sẽ chuyển sang nền tảng mới c&ugrave;ng một l&uacute;c.&nbsp;AMD sẽ &aacute;p dụng một Socket&nbsp;AM5 mới để hỗ trợ kiến ​​tr&uacute;c Zen4, trong khi Intel sẽ giới thiệu socket ho&agrave;n to&agrave;n mới được gọi l&agrave;&nbsp;&nbsp;LGA1700 sẽ thay thế cho&nbsp;LGA 1200&nbsp;hiện tại&nbsp;cho loạt bo mạch chủ&nbsp; 600 của m&igrave;nh. Socket&nbsp;LGA 1200 sẽ chỉ tồn tại hai thế hệ CPU (Comet Lake &amp; Rocket Lake).&nbsp;Socket LGA 1700 sẽ hỗ trợ &iacute;t nhất hai thế hệ CPU bao gồm Alder Lake v&agrave; Meteor Lake.</span></p>\r\n<p><span style=\"font-size: 12pt;\">Ch&iacute;nh thức&nbsp;ra mắt v&agrave;o nửa cuối năm 2021&nbsp;, c&aacute;c CPU Alder Lake sẽ l&agrave; d&ograve;ng m&aacute;y t&iacute;nh để b&agrave;n đầu ti&ecirc;n dựa tr&ecirc;n quy tr&igrave;nh 10nm.&nbsp;Bước nhảy l&ecirc;n 10nm sẽ đ&aacute;nh dấu sự kết th&uacute;c của kỷ nguy&ecirc;n 14nm đ&atilde; xuất hiện kể từ Broadwell v&agrave;o năm 2015.</span></p>\r\n<p><span style=\"font-size: 12pt;\">Một danh s&aacute;ch c&aacute;c ID thiết bị đ&atilde; được liệt k&ecirc; bởi&nbsp;Coelacanth-dream&nbsp;(th&ocirc;ng qua&nbsp;Videocardz&nbsp;) đ&atilde; bị r&ograve; rỉ trong&nbsp;Core&nbsp;Boot&nbsp;, chỉ ra một số cấu h&igrave;nh CPU Alder Lake v&agrave; cũng như định vị của c&aacute;c CPU n&agrave;y, nền tảng v&agrave; c&aacute;c biến thể đồ họa t&iacute;ch hợp.&nbsp;Ngo&agrave;i việc r&ograve; rỉ Coreboot, Videocardz cũng nhận được th&ocirc;ng tin rằng Alder Lake đ&atilde; chỉ ra rằng hỗ trợ bộ nhớ DDR5 tr&ecirc;n nền tảng m&aacute;y t&iacute;nh để b&agrave;n Intel v&agrave;o năm 2021.</span></p>\r\n<p><span style=\"font-size: 12pt;\">CPU Intel thế hệ thứ 12 Alder Lake</span></p>\r\n<p><span style=\"font-size: 12pt;\">C&aacute;c CPU Alder Lake l&agrave; cpu m&aacute;y t&iacute;nh để b&agrave;n 10nm đầu ti&ecirc;n của Intel, c&oacute; thiết kế kiến ​​tr&uacute;c lai.&nbsp;Alder Lake-S của Intel sẽ l&agrave; một con qu&aacute;i vật ho&agrave;n to&agrave;n kh&aacute;c so với bất kỳ thứ g&igrave; ch&uacute;ng ta đ&atilde; thấy từ Intel, sử dụng n&uacute;t quy tr&igrave;nh 10nm ++, đ&acirc;y l&agrave; một bước tiến h&oacute;a của n&uacute;t quy tr&igrave;nh 10nm + (c&ugrave;ng một n&uacute;t được sử dụng để chế tạo CPU Tiger Lake).&nbsp;D&ograve;ng sản phẩm Core thế hệ thứ 12 của Alder Lake-S sẽ c&oacute; phương ph&aacute;p thiết kế mới, hỗ trợ sự kết hợp giữa c&aacute;c l&otilde;i lớn v&agrave; nhỏ như ch&uacute;ng t&ocirc;i đ&atilde; b&aacute;o c&aacute;o trước đ&oacute;.&nbsp;Ba cấu h&igrave;nh của CPU Alder Lake-S đ&atilde; bị r&ograve; rỉ bao gồm:</span></p>\r\n<ul>\r\n<li><span style=\"font-size: 12pt;\">Alder Lake-S (8 + 8 + 1) Cấu h&igrave;nh 125W</span></li>\r\n<li><span style=\"font-size: 12pt;\">Cấu h&igrave;nh Alder Lake-S (8 + 8 + 1) 80W</span></li>\r\n<li><span style=\"font-size: 12pt;\">Cấu h&igrave;nh Alder Lake-S (6 + 0 + 1) 80W</span></li>\r\n</ul>\r\n<p><span style=\"font-size: 12pt;\">Như bạn c&oacute; thể thấy, c&aacute;c CPU sẽ c&oacute; nhiều cấu h&igrave;nh kh&aacute;c nhau với tối đa 8 l&otilde;i hiệu suất cao v&agrave; 8 l&otilde;i được tối ưu h&oacute;a hiệu quả.&nbsp;C&oacute; c&aacute;c biến thể mở kh&oacute;a với 125W v&agrave; c&aacute;c biến thể kh&oacute;a với TDP 80W.&nbsp;Mặc d&ugrave; phương ph&aacute;p thiết kế chip kh&ocirc;ng phải l&agrave; bất cứ điều g&igrave; mới v&igrave; ch&uacute;ng ta đ&atilde; thấy một số SOC di động c&oacute; hệ thống ph&acirc;n cấp l&otilde;i tương tự, nhưng chắc chắn sẽ rất th&uacute; vị khi thấy sự xuất hiện tương tự tr&ecirc;n d&ograve;ng CPU m&aacute;y t&iacute;nh để b&agrave;n hiệu năng cao.</span></p>\r\n<p><span style=\"font-size: 12pt;\">Theo những g&igrave; ch&uacute;ng ta biết cho đến nay, Intel c&oacute; kế hoạch bao gồm một hỗn hợp c&aacute;c l&otilde;i CPU dựa tr&ecirc;n c&aacute;c IP kh&aacute;c nhau.&nbsp;C&aacute;c CPU Alder Lake sẽ đi k&egrave;m với l&otilde;i \'Cove\' hiệu suất cao ti&ecirc;u chuẩn v&agrave; l&otilde;i \'Atom\' nhỏ hơn nhưng hiệu quả hơn.&nbsp;Đ&acirc;y&nbsp;<span class=\"skimlinks-unlinked\">big.SMALL</span>&nbsp;phương ph&aacute;p thiết kế đ&atilde; được t&iacute;ch hợp tr&ecirc;n điện thoại th&ocirc;ng minh trong thời gian gần đ&acirc;y&nbsp;nhưng với Alder Lake&nbsp;đ&acirc;y sẽ l&agrave; lần đầu ti&ecirc;n ch&uacute;ng ta nh&igrave;n thấy n&oacute; hoạt động&nbsp;trong ph&acirc;n kh&uacute;c hiệu năng cao</span></p>\r\n<p><span style=\"font-size: 12pt;\">Ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; bất kỳ th&ocirc;ng tin cụ thể n&agrave;o về thế hệ kiến ​​tr&uacute;c \'Cove\' hoặc \'Atom\' n&agrave;o m&agrave; Intel dự định sử dụng cho c&aacute;c CPU Alder Lake của m&igrave;nh nhưng lộ tr&igrave;nh của họ chỉ ra rằng t&iacute;nh khả dụng của kiến ​​tr&uacute;c Golden Cove v&agrave; Gracement v&agrave;o năm 2021. C&oacute; thể ch&uacute;ng ta sẽ thấy c&aacute;c l&otilde;i n&agrave;y trước hết trong c&aacute;c hoạt&nbsp;động tr&ecirc;n nền tảng CPU m&aacute;y t&iacute;nh để b&agrave;n.&nbsp;Tuy nhi&ecirc;n, nếu Intel sử dụng kiến ​​tr&uacute;c hiện c&oacute; cho Alder Lake th&igrave;&nbsp;họ chắc chắn sẽ sử dụng l&otilde;i Willow Cove v&agrave; Tracemont.</span></p>', 'images/posts/intel.jpg', 1, 23, NULL, '2022-07-17 03:25:33', '2022-07-18 04:18:56', 'Công khai', 'intel-core-the-he-thu-12-alder-lake-s-ho-tro-bo-nho-ddr5'),
(20, 'Top 5 cấu hình máy tính chuyên làm đồ họa dưới 20 triệu', '<p style=\"line-height: 1.5;\">Với sự b&ugrave;ng nổ của Cuộc c&aacute;ch mạng C&ocirc;ng nghiệp 4.0 th&igrave; nhu cầu sử dụng m&aacute;y t&iacute;nh của con người ng&agrave;y một tăng cao, n&oacute; gần như trở th&agrave;nh c&ocirc;ng cụ kh&ocirc;ng thể thiếu trong đời sống. Tuy nhi&ecirc;n, lựa chọn sản phẩm n&agrave;o cho ph&ugrave; hợp với nhu cầu sử dụng v&agrave; mức ng&acirc;n s&aacute;ch của m&igrave;nh lại l&agrave; việc kh&ocirc;ng hề dễ d&agrave;ng. Dưới đ&acirc;y, H&agrave; Linh Computer sẽ đưa ra Top 5 cấu h&igrave;nh m&aacute;y t&iacute;nh chuy&ecirc;n l&agrave;m đồ họa dưới 20 triệu đang được ưa chuộng nhất hiện nay để c&aacute;c bạn c&oacute; được sự lựa chọn hợp l&yacute; ph&ugrave; hợp với t&uacute;i tiền v&agrave; nhu cầu sử dụng của m&igrave;nh. Đặc biệt, ngo&agrave;i mức gi&aacute; rất ưu đ&atilde;i bạn c&ograve;n được tặng ngay 1 Combo b&agrave;n ph&iacute;m DareU LK135 v&agrave; Chuột DAREU LM130 khi mua sản phẩm.</p>\r\n<p><span style=\"font-size: 12pt;\">1. PC Intel I5 10500/16GB Ram/ VGA RX 550 4GB</span></p>\r\n<p>Cấu h&igrave;nh n&agrave;y sử dụng CPU Intel Core i5 10500 6 nh&acirc;n 12 luồng với xung nhịp Turbo Boost l&ecirc;n đến 4.6GHz tối ưu cho c&aacute;c phần mềm Adobe ,Dựng Phim , Edit Video Chỉnh sửa ảnh v&agrave; thiết kế dựng h&igrave;nh 3D</p>\r\n<div class=\"ddict_btn\" style=\"top: 250.8px; left: 1132.8px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'images/posts/casepc.jpg', 1, 24, NULL, '2022-07-17 03:45:10', '2022-07-18 04:34:30', 'Công khai', 'top-5-cau-hinh-may-tinh-chuyen-lam-do-hoa-duoi-20-trieu'),
(21, 'So sánh bo mạch chủ B550 với X570 và B450', '<p style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">D&ograve;ng CPU Ryzen 4000 được x&acirc;y dựng dựa tr&ecirc;n kiến tr&uacute;c Zen 3 của AMD dự kiến sẽ ra mắt v&agrave;o cuối năm nay. Do đ&oacute;, l&agrave; một trong những sản phẩm tương th&iacute;ch với bộ vi xử l&yacute; n&agrave;y,&nbsp;<a href=\"https://halinhcomputer.vn/tim-kiem.html?key=B550\">bo mạch chủ B550</a>&nbsp;đang nhận được rất nhiều sự quan t&acirc;m từ ph&iacute;a người d&ugrave;ng. Vậy bo mạch chủ n&agrave;y c&oacute; g&igrave; kh&aacute;c biệt so với c&aacute;c bo mạch chủ kh&aacute;c c&ugrave;ng socket AM4 như X570 v&agrave; B450. H&atilde;y c&ugrave;ng&nbsp;H&agrave; Linh Computer&nbsp;t&igrave;m hiểu cụ thể hơn trong b&agrave;i viết n&agrave;y nh&eacute;!</span></p>\r\n<p style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">Về mặt kỹ thuật, X570 l&agrave; bo mạch chủ cao cấp hơn một ch&uacute;t v&agrave; c&oacute; thể cung cấp nhiều t&iacute;nh năng tổng thể, khả năng kết nối v&agrave; t&iacute;nh linh hoạt hơn so với B550. Tuy nhi&ecirc;n, điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; bạn n&ecirc;n mua X570 thay v&igrave; B550, v&igrave; bo mạch chủ B550 \"cao cấp\" c&oacute; thể mạnh ngang với bo mạch chủ X570. &nbsp;V&agrave; dưới đ&acirc;y l&agrave; những kh&aacute;c biệt tổng thể giữa bo mạch chủ B550 v&agrave; X570:</span></p>\r\n<p style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://halinhcomputer.vn/uploads/images/bai-viet/b550-vs-x570-vs-b450/b450-vs-b550.jpg\" alt=\"\" /></span></p>\r\n<p style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\"><em>So s&aacute;nh bo mạch chủ B550 với X570</em></span></p>\r\n<ul style=\"list-style-type: circle;\">\r\n<li style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">X570 hỗ trợ từ CPU Ryzen 2000 series trở l&ecirc;n c&ograve;n B550 chỉ hỗ trợ từ CPU Ryzen 3000 series (ngoại trừ c&aacute;c bản c&oacute; t&iacute;ch hợp đồ họa)</span></li>\r\n<li style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">Bo mạch chủ B550 c&oacute; thể hỗ trợ nhiều ổ SSD M.2, nhưng chỉ một trong những khe cắm đ&oacute; sẽ tương th&iacute;ch với PCIe 4.0 (ngoại trừ Gigabyte B550 Aorus Master) c&ograve;n khe cắm thứ 2 tương th&iacute;ch với PCIe 3.0 trong khi bo mạch chủ X570 c&oacute; hỗ trợ khe cắm PCIe 4.0 M.2 k&eacute;p</span></li>\r\n<li style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">X570 sử dụng li&ecirc;n kết chipset PCIe 4.0 c&ograve;n B550 l&agrave; PCIe 3.0</span></li>\r\n<li style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">Hầu hết tất cả c&aacute;c bo mạch chủ X570 đều c&oacute; một quạt chipset nhỏ t&iacute;ch hợp để gi&uacute;p bo mạch lu&ocirc;n m&aacute;t mẻ, trong khi bo mạch B550 kh&ocirc;ng c&oacute; quạt chipset v&igrave; ch&uacute;ng c&oacute; mức ti&ecirc;u thụ điện năng thấp hơn v&agrave; tỏa nhiệt &iacute;t hơn.</span></li>\r\n<li style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">X570 hỗ trợ nhiều cổng SATA hơn so với B550</span></li>\r\n<li style=\"line-height: 1.5;\"><span style=\"font-size: 12pt;\">Bo mạch chủ B550 chỉ c&oacute; cổng LAN 1GB hoặc 2.5GB, trong khi một số bo mạch X570 c&oacute; thể bao gồm cổng LAN 5GB hoặc 10GB cực nhanh.</span></li>\r\n</ul>', 'images/posts/dual-gpu-support.png', 1, 23, NULL, '2022-07-17 16:00:47', '2022-07-18 04:23:31', 'Công khai', 'so-sanh-bo-mach-chu-b550-voi-x570-va-b450'),
(22, 'Mua bao nhiêu sản phầm - quà bấy nhiêu cho các sản phẩm asus gaming', '<p><span style=\"font-size: 12pt;\">Chương tr&igrave;nh khuyến m&atilde;i cực khủng &ldquo;mua nhiều bao nhi&ecirc;u - qu&agrave; nhiều bấy nhi&ecirc;u&rdquo; &nbsp;với rất nhiều qu&agrave; tặng c&oacute; gi&aacute; trị cực sốc ch&agrave;o đ&oacute;n sự kiện mở b&aacute;n bo</span></p>\r\n<p><span style=\"font-size: 12pt;\"> mạch chủ thế hệ mới ASUS ROG Z490 series d&agrave;nh cho qu&yacute; kh&aacute;ch h&agrave;ng l&agrave; người d&ugrave;ng cuối từ&nbsp;ng&agrave;y 20/05/2020 v&agrave; kết th&uacute;c v&agrave;o ng&agrave;y 20/06/2020.</span></p>\r\n<p><span style=\"font-size: 12pt;\">Qu&agrave; tặng sẽ ưu ti&ecirc;n cho kh&aacute;ch h&agrave;ng đăng k&iacute; sớm v&agrave; sẽ th&ocirc;ng b&aacute;o khi hết qu&agrave; tặng.&nbsp; Qu&yacute; kh&aacute;ch h&agrave;ng vui l&ograve;ng xem chi tiết c&aacute;c khuyến m&atilde;i v&agrave; đăng k&iacute; nhận</span></p>\r\n<p><span style=\"font-size: 12pt;\"> qu&agrave; tại đ&acirc;y :</span></p>\r\n<p><span style=\"font-size: 12pt;\">1- Bo mạch chủ:&nbsp;</span></p>\r\n<p><span style=\"font-size: 12pt;\"><img src=\"https://halinhcomputer.vn/uploads/images/bai-viet/km-asus/promotion-z490-785x466px-mb.jpg\" alt=\"\" /></span></p>\r\n<p><span style=\"font-size: 12pt;\">- Qu&agrave; tặng v&agrave; sản phẩm &aacute;p dụng:</span></p>\r\n<p><span style=\"font-size: 12pt;\">- Mua bo mạch chủ&nbsp;ROG Z490 Maximus&nbsp;series&nbsp;nhận ngay:&nbsp;Vali ROG (m&agrave;u t&ugrave;y chọn)</span></p>\r\n<p><span style=\"font-size: 12pt;\">- Mua bo mạch chủ&nbsp;ASUS Z490 Prime/ TUF/ ProArt/ ROG Strix series&nbsp;nhận ngay:&nbsp;&Aacute;o kho&aacute;c ROG (m&agrave;u t&ugrave;y chọn) + combo 2 khẩu trang ROG</span></p>\r\n<p><span style=\"font-size: 12pt;\">2- Card đồ họa:&nbsp;</span></p>\r\n<p><span style=\"font-size: 12pt;\">- Qu&agrave; tặng v&agrave; sản phẩm &aacute;p dụng :</span></p>\r\n<p><span style=\"font-size: 12pt;\">- Mua card đồ họa ASUS ROG Geforce&nbsp;RTX20/ RTX20(S)&nbsp;series nhận ngay:&nbsp;&Aacute;o kho&aacute;c ROG (m&agrave;u t&ugrave;y chọn)</span></p>\r\n<h2><span style=\"font-size: 12pt;\"><img src=\"https://halinhcomputer.vn/uploads/images/bai-viet/km-asus/promotion-z490-785x466px-vga.jpg\" alt=\"\" /></span></h2>\r\n<p><span style=\"font-size: 12pt;\">3- M&agrave;n h&igrave;nh chơi game:&nbsp;</span></p>\r\n<p><span style=\"font-size: 12pt;\">- Qu&yacute; kh&aacute;ch h&agrave;ng nhận ngay Tai nghe in-ear ROG Cetra Core trị gi&aacute; 1,190,000đ khi mua :</span></p>\r\n<p><span style=\"font-size: 12pt;\">- M&agrave;n h&igrave;nh chơi game&nbsp;ASUS TUF Gaming VG27AQ 27\" IPS 2K HDR10 165Hz 1ms MPRT G-SYNC Compatible 2 Loa FreeSync</span></p>\r\n<p><span style=\"font-size: 12pt;\">- M&agrave;n h&igrave;nh chơi game&nbsp;ASUS TUF Gaming VG32VQ 31.5\" 2560x1440 144Hz 1ms MPRT ELMB-Sync, HDR10</span></p>\r\n<div class=\"ddict_btn\" style=\"top: 1507px; left: 1132.8px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', 'images/posts/promotion-z49.jpg', 1, 22, NULL, '2022-07-17 16:04:31', '2022-07-18 04:26:52', 'Công khai', 'mua-bao-nhieu-san-pham-qua-bay-nhieu-cho-cac-san-pham-asus-gaming');

